  "spacing": 3,

  "modules-left": ["custom/launcher", "hyprland/workspaces"],
  "modules-center": ["clock"],
  "modules-right": ["pulseaudio", "cpu", "memory", "battery", "tray"],

  "custom/launcher": {
    "format": "ÔåÉ",
    "tooltip": false,
    "on-click": "rofi -show drun",
    "on-click-right": "rofi -show run"
  },

  "hyprland/workspaces": {
    "disable-scroll": true,
    "all-outputs": true,
    "format": "{icon}",
    "format-icons": {
      "1": "Û∞àπ",
      "2": "Û∞ñü", 
      "3": "Û∞äñ",
      "4": "Û∞ôØ",
      "5": "Û∞ùö",
      "urgent": "Û∞óó",
      "focused": "Û∞ÆØ",
      "default": "Û∞ä†"
    }
  },

  "clock": {
    "format": "{:%H:%M}",
    "tooltip-format": "<tt>{calendar}</tt>"
  },

  "cpu": {
    "format": "Û∞ª† {usage}%",
    "interval": 5
  },

  "memory": {
    "format": "Û∞çõ {percentage}%", 
    "interval": 5
  },

  "battery": {
    "format": "{icon} {capacity}%",
    "format-icons": ["Û∞Çé", "Û∞Å∫", "Û∞Åª", "Û∞Åº", "Û∞ÅΩ", "Û∞Åæ", "Û∞Åø", "Û∞ÇÄ", "Û∞ÇÅ", "Û∞ÇÇ", "Û∞Åπ"],
    "format-charging": "Û∞ÇÑ {capacity}%",
    "interval": 5
  },

  "pulseaudio": {
    "format": "{icon} {volume}%",
    "format-muted": "Û∞ñÅ Muted", 
    "format-icons": ["Û∞ïø", "Û∞ñÄ", "Û∞ïæ"],
    "scroll-step": 5
  },

  "tray": {
    "spacing": 4
  }
}

* {
    border: none;
    border-radius: 0;
    font-family: "JetBrains Mono Nerd Font", "Iosevka Nerd Font", "Fira Code", sans-serif;
    font-size: 11px;
    min-height: 0;
}

window#waybar {
    background: rgba(30, 30, 46, 0.85);
    color: #cdd6f4;
    border-bottom: none;
    border-radius: 14px;
    margin: 6px 6px 0px 6px;
}

/* Floating shadow effect for the bar only */
window#waybar {
    box-shadow: 
        0 4px 16px 0 rgba(0, 0, 0, 0.15),
        inset 0 1px 0 rgba(255, 255, 255, 0.1);
}

/* Application launcher - no background */
#custom-launcher {
    padding: 0 8px;
    margin: 0 8px 0 12px;
    background: transparent;
    color: #89b4fa;
    font-size: 16px;
}

#custom-launcher:hover {
    color: #cdd6f4;
}

/* Workspaces - no background */
#workspaces {
    background: transparent;
}

#workspaces button {
    padding: 0 8px;
    background: transparent;
    color: #6c7086;
    margin: 0 2px;
    transition: all 0.2s ease-in-out;
    font-size: 12px;
}

#workspaces button.focused {
    background: transparent;
    color: #89b4fa;
}

#workspaces button:hover {
    background: transparent;
    color: #cdd6f4;
}

/* All modules - no background, just text/icons */
#clock, #cpu, #memory, #battery, #pulseaudio, #tray {
    padding: 0 8px;
    margin: 0 4px;
    background: transparent;
    color: #cdd6f4;
    border-radius: 0;
}

#clock {
    color: #89b4fa;
    font-weight: bold;
}

#battery.charging {
    color: #a6e3a1;
}

#battery.warning:not(.charging) {
    color: #f9e2af;
}

#battery.critical:not(.charging) {
    color: #f38ba8;
    animation: blink 1.5s ease-in-out infinite alternate;
}

@keyframes blink {
    to {
        color: #ff5555;
    }
}

#pulseaudio.muted {
    color: #f38ba8;
}

#tray {
    background: transparent;
}

* {
    border: none;
    border-radius: 0;
    font-family: "JetBrains Mono Nerd Font", "Iosevka Nerd Font", "Fira Code", sans-serif;
    font-size: 12px;
    min-height: 0;
}

window#waybar {
    background: linear-gradient(135deg, rgba(15, 30, 55, 0.92) 0%, rgba(20, 40, 70, 0.92) 100%);
    color: #e0e8ff;
    border-bottom: none;
    border-radius: 16px;
    margin: 8px 8px 0px 8px;
    border: 1px solid rgba(100, 150, 255, 0.2);
}

/* Floating shadow effect for the bar */
window#waybar {
    box-shadow: 
        0 8px 24px 0 rgba(30, 80, 150, 0.25),
        inset 0 1px 0 rgba(150, 180, 255, 0.15),
        0 0 40px rgba(100, 150, 255, 0.1);
}

/* Application launcher */
#custom-launcher {
    padding: 0 12px;
    margin: 0 8px 0 12px;
    background: linear-gradient(135deg, rgba(59, 130, 246, 0.2), rgba(139, 180, 250, 0.1));
    color: #60a5fa;
    font-size: 18px;
    border-radius: 8px;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

#custom-launcher:hover {
    color: #93c5fd;
    background: linear-gradient(135deg, rgba(59, 130, 246, 0.35), rgba(139, 180, 250, 0.2));
    transform: scale(1.05);
}

/* Workspaces */
#workspaces {
    background: transparent;
    margin: 0 12px;
}

#workspaces button {
    padding: 4px 10px;
    background: transparent;
    color: #64748b;
    margin: 0 4px;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    font-size: 13px;
    border-radius: 6px;
}

#workspaces button.focused {
    background: linear-gradient(135deg, rgba(59, 130, 246, 0.3), rgba(100, 150, 255, 0.2));
    color: #60a5fa;
    box-shadow: 0 0 12px rgba(59, 130, 246, 0.3);
}

#workspaces button:hover {
    background: rgba(100, 150, 255, 0.15);
    color: #93c5fd;
}

/* Clock */
#clock {
    padding: 6px 12px;
    margin: 0 4px;
    color: #60a5fa;
    font-weight: bold;
    background: linear-gradient(135deg, rgba(59, 130, 246, 0.25), rgba(100, 150, 255, 0.15));
    border-radius: 8px;
    box-shadow: 0 0 15px rgba(59, 130, 246, 0.2);
}

/* All other modules */
#cpu, #memory, #battery, #network, #pulseaudio, #tray {
    padding: 6px 12px;
    margin: 0 4px;
    background: linear-gradient(135deg, rgba(30, 58, 138, 0.3), rgba(59, 130, 246, 0.15));
    color: #cbd5e1;
    border-radius: 8px;
    transition: all 0.3s ease;
}

#network {
    color: #93c5fd;
}

#network:hover {
    background: linear-gradient(135deg, rgba(59, 130, 246, 0.35), rgba(100, 150, 255, 0.25));
    color: #bfdbfe;
}

#battery {
    color: #60a5fa;
}

#battery.charging {
    color: #4ade80;
    background: linear-gradient(135deg, rgba(34, 197, 94, 0.2), rgba(74, 222, 128, 0.15));
}

#battery.warning:not(.charging) {
    color: #facc15;
    background: linear-gradient(135deg, rgba(250, 204, 21, 0.2), rgba(253, 224, 71, 0.15));
}

#battery.critical:not(.charging) {
    color: #ef4444;
    background: linear-gradient(135deg, rgba(239, 68, 68, 0.2), rgba(248, 113, 113, 0.15));
    animation: blink 1.5s ease-in-out infinite alternate;
}

@keyframes blink {
    to {
        color: #fca5a5;
    }
}

#cpu:hover, #memory:hover, #battery:hover {
    background: linear-gradient(135deg, rgba(59, 130, 246, 0.3), rgba(100, 150, 255, 0.2));
    color: #93c5fd;
}

#pulseaudio {
    color: #93c5fd;
}

#pulseaudio:hover {
    background: linear-gradient(135deg, rgba(59, 130, 246, 0.3), rgba(100, 150, 255, 0.2));
    color: #bfdbfe;
}

#pulseaudio.muted {
    color: #ef4444;
    background: linear-gradient(135deg, rgba(239, 68, 68, 0.2), rgba(248, 113, 113, 0.15));
}

#tray {
    background: transparent;
    margin-right: 8px;
}

/* Smooth transitions */
#cpu, #memory, #network, #pulseaudio {
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

