* {
    border: none;
    border-radius: 8px;
    padding: 0;
    margin: 0;
    min-height: 0;
    font-family: "JetBrains Mono", "Noto Sans Arabic", "Symbols Nerd Font";
    font-size: 14px;
    font-weight: 600;
}

window#waybar {
    background: rgba(5, 5, 20, 0.7);
    backdrop-filter: blur(10px);
    color: #00d9ff;
    border: 1px solid rgba(0, 136, 255, 0.2);
    border-radius: 8px;
}

/* Active workspace */
#workspaces button.active {
    background: linear-gradient(135deg, #0088ff, #0055aa);
    color: #ffffff;
    padding: 0 12px;
    margin: 4px 3px;
    border-radius: 6px;
    box-shadow: 0 0 10px rgba(0, 136, 255, 0.4);
}

/* Inactive workspace */
#workspaces button {
    background: rgba(0, 136, 255, 0.1);
    color: #00a0ff;
    padding: 0 12px;
    margin: 4px 3px;
    border-radius: 6px;
    transition: all 0.3s ease;
}

#workspaces button:hover {
    background: rgba(0, 136, 255, 0.25);
    color: #00d9ff;
    box-shadow: 0 0 8px rgba(0, 136, 255, 0.3);
}

/* Urgent workspace */
#workspaces button.urgent {
    background: rgba(255, 100, 100, 0.8);
    color: #ffffff;
}

/* Window title */
#window {
    background: rgba(0, 136, 255, 0.1);
    color: #00d9ff;
    padding: 0 15px;
    margin: 4px 6px;
    border-radius: 6px;
    border-left: 3px solid #0088ff;
}

/* Clock */
#clock {
    background: rgba(0, 100, 200, 0.15);
    color: #00d9ff;
    padding: 0 20px;
    margin: 4px 0;
    border-radius: 6px;
    border: 1px solid rgba(0, 136, 255, 0.3);
    font-weight: 700;
}

/* Network */
#network {
    background: rgba(0, 136, 255, 0.1);
    color: #00d9ff;
    padding: 0 12px;
    margin: 4px 3px;
    border-radius: 6px;
    border-left: 3px solid #00cc00;
}

#network.disconnected {
    background: rgba(255, 100, 100, 0.1);
    color: #ff6464;
    border-left: 3px solid #ff6464;
}

/* Pulseaudio */
#pulseaudio {
    background: rgba(0, 136, 255, 0.1);
    color: #00d9ff;
    padding: 0 12px;
    margin: 4px 3px;
    border-radius: 6px;
    border-left: 3px solid #ff8800;
}

#pulseaudio.muted {
    background: rgba(255, 100, 100, 0.1);
    color: #ff6464;
    border-left: 3px solid #ff6464;
}

/* Battery */
#battery {
    background: rgba(0, 136, 255, 0.1);
    color: #00d9ff;
    padding: 0 12px;
    margin: 4px 3px;
    border-radius: 6px;
    border-left: 3px solid #00ff88;
}

#battery.charging {
    color: #00ff88;
    border-left: 3px solid #00ff88;
}

#battery.warning {
    background: rgba(255, 200, 0, 0.1);
    color: #ffcc00;
    border-left: 3px solid #ffcc00;
}

#battery.critical {
    background: rgba(255, 100, 100, 0.2);
    color: #ff6464;
    border-left: 3px solid #ff6464;
}

/* Backlight */
#backlight {
    background: rgba(0, 136, 255, 0.1);
    color: #00d9ff;
    padding: 0 12px;
    margin: 4px 3px;
    border-radius: 6px;
    border-left: 3px solid #ffcc00;
}

/* Power Profiles */
#power-profiles-daemon {
    background: rgba(0, 136, 255, 0.1);
    color: #00d9ff;
    padding: 0 12px;
    margin: 4px 3px;
    border-radius: 6px;
    border-left: 3px solid #cc00ff;
}

/* System Tray */
#tray {
    background: rgba(0, 136, 255, 0.05);
    padding: 0 12px;
    margin: 4px 3px;
    border-radius: 6px;
    border-left: 3px solid #0088ff;
}

#tray > .passive {
    color: #008899;
}

#tray > .active {
    color: #00d9ff;
}

#tray > .needs-attention {
    color: #ff6464;
    animation: blink 1.5s infinite ease-in-out;
}

@keyframes blink {
    0%, 100% { color: #ff6464; }
    50% { color: #ff9999; }
}

/* Hover effects */
#network:hover,
#pulseaudio:hover,
#battery:hover,
#backlight:hover,
#power-profiles-daemon:hover {
    background: rgba(0, 136, 255, 0.2);
    box-shadow: 0 0 8px rgba(0, 136, 255, 0.3);
}

/* Scrolling tooltip */
tooltip {
    background: rgba(5, 5, 20, 0.95);
    border: 1px solid rgba(0, 136, 255, 0.4);
    color: #00d9ff;
    border-radius: 6px;
    padding: 8px;
}

tooltip label {
    color: #00d9ff;
}
"}

{"filePath": "/home/ali/example config/.config/rofi/launcher.rasi", "content": "/* ╔════════════════════════════════════════════════════════╗
