    "temperature": {
        "thermal-zone": 0,
        "hwmon-path": "/sys/class/hwmon/hwmon0/temp1_input",
        "format": "󰔏 {temperatureC}°C",
        "format-critical": "󰻗 {temperatureC}°C",
        "critical-threshold": 80,
        "interval": 5
    },

